const jwt = require('jsonwebtoken');

/**
 *  Authentication middleware
 *  It validates the token sent by the user and proceeds if valid else reject
 *
 * @param {Object} req The request object from the client
 * @param {Object} res  The response object from the server
 * @param {*} next A function called when user is unthenticated
 * @returns
 */
module.exports = (req, res, next) => {
    const { authorization } = req.headers;
    if(!authorization) {
        return res.status(401).send('unauthorized');
    }

    jwt.verify(authorization, 'secret', (err, decoded) => {
        if(err && !decoded) {
            return res.status(401).send('unauthorized');
        }else {
            req.body.decoded = decoded;
            next();
        }
    });
};

const jwt = require('jsonwebtoken');
const jsonPatch = require('fast-json-patch');
const path = require('path');
const request = require('request');
const fs = require('fs');
const sharp = require('sharp');

module.exports = {
    /**
     * Generates a token for future authentication
     * @param {String} username Username of signedin user.
     * @returns {string} A token string.
     */
    generateToken(username) {
        return jwt.sign({ username }, 'secret', { expiresIn: '1h' });
    },

    /**
     * Apply patch to a document
     *
     * @param {Object} document The document to which the patch will be applied
     * @param {(Object|Array)} patch The patch to apply to the document
     * @returns {Object} Result from the applied patch
     */
    applyPatch(document, patch) {
        if(patch.length !== undefined) {
            return jsonPatch.applyPatch(document, patch).newDocument;
        }else {
            return jsonPatch.applyOperation(document, patch).newDocument;
        }
    },

    /**
     * Check if the url is an image url
     *
     * @param {String} imageUrl
     * @returns Boolean if imageUrl is valid or not
     */
    validateImageUrl(imageUrl) {
        if(['.jpeg', '.png', '.jpg'].indexOf(path.extname(imageUrl)) === -1) {
            return false;
        }
        return true;
    },

    /**
     * Download the image sent by the user.
     *
     * @param {String} imageUrl image url
     * @param {*} callback A function that runs if this function is done excuting
     */
    downloadImage(imageUrl, callback) {
        request
            .get(imageUrl)
            .on('error', (err) => callback(err))
            .pipe(fs.createWriteStream(`img/${ path.parse(imageUrl).name }${ path.extname(imageUrl) }`))
            .on('close', () => callback(null, path.join(__dirname, `/img/${ path.parse(imageUrl).name }${ path.extname(imageUrl) }`)));     
    },

    /**
     * Resizes an image 
     *
     * @param {String} imageUrl
     * @param {*} callback Function containing the resized image
     */
    resizeImage(imageUrl, callback) {
        sharp(imageUrl)
            .resize(50, 50)
            .toFile(path.join(__dirname, `/img/${path.parse(imageUrl).name}_resized${path.extname(imageUrl)}`), () => {
                return callback(null, path.join(__dirname, `/img/${path.parse(imageUrl).name}_resized${path.extname(imageUrl)}`));
            });
    }
};

const winston = require('winston');
const logger = winston.createLogger({
    level: 'info',
    format: winston.format.json(),
    defaultMeta: { service: 'user-service' },
    transports: [
        new winston.transports.File({ filename: 'error.log', level: 'error' }),
        new winston.transports.File({ filename: 'combined.log' })
    ]
});

if (process.env.NODE_ENV !== 'production') {
    logger.add(new winston.transports.Console({
        format: winston.format.simple()
    }));
}

module.exports = logger;

/** Local modules import */
const _helper = require('./helperFunc');
const auth = require('./auth');
const logger = require('./logger');

/**
 *  Router configuration
 *
 * @param {Object} router
 * @returns {Object} Router object
 */
const routes = (router) => {
    router.route('/login')
        .post((req, res) => {
            let { username, password } = req.body;
            if(!username || !password) {
                return res.status(401).send('Invalid Field');
            }
            const token = _helper.generateToken(username);
            res.status(200).json({ success: true, data: { username, token }  });
        });
    
    router.route('/patch')
        .patch(auth, (req, res) => {
            let { document, patch } = req.body;
            if(!document || typeof(document) !== 'object' || !patch || typeof(patch) !== 'object') {
                return res.status(401).send('Invalid Field');
            }
            const patchResult = _helper.applyPatch(document, patch);
            const { username } = req.body.decoded;
            const token = _helper.generateToken(username);
            res.status(201).json({ success: true, data: { username, token, patchResult }  });
        });

    router.route('/thumbnail')
        .post(auth, (req, res) => {
            const { url } = req.body;
            if(!url || !_helper.validateImageUrl(url)) {
                return res.status(401).send('Invalid Field');
            }

            _helper.downloadImage(url, (err, image) => {
                if(err) {
                    logger.log('error', err);
                    return res.status(401).send('Something went wrong');
                }
                _helper.resizeImage(image, (err, data) => {
                    if(!err) {
                        logger.log('error', err);
                        res.status(200).sendFile(data);
                    }
                });
            });
        });
        
    return router;
};

module.exports = routes;

/** Core modules */
const express = require('express');

/** Local modules */
const logger = require('./logger');
const routes = require('./router');

/** Setting up app, router and defining port */
const router = express.Router();
const app = express();
const PORT = process.env.PORT || 3000;

/** Applying middlewares */
app.use(express.urlencoded({ extended: false }));
app.use(express.json());
app.use(routes(router));
app.use('**', (req,res) => res.status(404).send('Not Found'));

/** Listening to port */
app.listen(PORT, () => logger.log({
    level: 'info',
    message: `App running on port: ${ PORT }`
}));

const chai = require('chai');
const chaiHttp = require('chai-http');
chai.use(chaiHttp);
const assert = require('chai').assert;
const _helper = require('../helperFunc');
const path = require('path');

/** @test {_helper} */
describe('Helper function', function() {
    /** @test {_helper.generateToken()} */
    describe('_helper.generateToken', function() {
        const token = _helper.generateToken('Alabi');

        it('Should return a string', function() {
            assert.isString(token, 'Token for authentication');
        });

        it('should have a length of 3 when splited with the . delimeter', function() {
            assert.equal(token.split('.').length, 3, 'Three parts of a token');
        });
    });

    /** @test {_helper.applyPatch()} */
    describe('_helper.applyPatch', function() {
        const document = { firstName: 'Albert', contactDetails: { phoneNumbers: [] } };
        const patch = [
            { 'op': 'replace', 'path': '/firstName', 'value': 'Joachim' },
            { 'op': 'add', 'path': '/lastName', 'value': 'Wester' },
            { 'op': 'add', 'path': '/contactDetails/phoneNumbers/0', 'value': { 'number': '555-123' }  }
        ];

        const newDoc = _helper.applyPatch(document, patch);
        it('should return type object', function() {
            assert.typeOf(newDoc, 'object', 'Modified doc');
        });
    });

    /** @test {_helper.validateImageUrl()} */
    describe('_helper.validateImageUrl', function() {
        it('Should return a boolean', function() {
            assert.isBoolean(_helper.validateImageUrl('example.png'), 'True or false');
        });

        it('Should return false', function() {
            assert.isFalse(_helper.validateImageUrl('example.html'));
        });
    });

    /** @test {_helper.downloadImage()} */
    describe('_helper.downloadImage', function() {
        it('Should download the image', function() {
            _helper.downloadImage('https://homepages.cae.wisc.edu/~ece533/images/cat.png', function(err, downloadedPath) {
                assert.equal(downloadedPath, path.join(__dirname, '../img/cat.png'));
            });
        });
    });

    /** @test {_helper.resizeImage()} */
    describe('_helper.resizeImage', function() {
        it('Should resize the cat image', function() {
            _helper.resizeImage( path.join(__dirname, '../img/cat.png'), function(err, resizedPath) {
                assert.equal(resizedPath, path.join(__dirname, '../img/cat_resized.png'));
            });
        });
    });
});

const chai = require('chai');
const chaiHttp = require('chai-http');
chai.use(chaiHttp);
const assert = require('chai').assert;

/** @test {API} */
describe('API', function() {
    const url = 'localhost:3000';
    /** @test {API#/login} */
    describe('/login', function() {
        it('Should pass some assertions', function(done) {
            chai.request(url)
                .post('/login')
                .send({
                    username: 'Alabi',
                    password: 'pass'
                })
                .end((err, res) => {
                    const { body, status } = res;
                    assert.equal(status, 200, 'Status code for the request');
                    assert.isNull(err, 'should not have any error');
                    assert.typeOf(body, 'object', 'Response object');
                    assert.isTrue(body.data.hasOwnProperty('token'), 'Token returned for authentication');
                    done();
                });
        });
    });

    /** @test {API#/patch} */
    describe('/patch', function() {
        it('Should be protected and reject this request with a status code of 401', function(done) {
            chai.request(url)
                .patch('/patch')
                .end((err, res) => {
                    const { text, status } = res;
                    assert.equal(text, 'unauthorized');
                    assert.equal(status, 401, 'Unauthorized');
                    done();
                });
        });

        it('Should authenticate and respond with 201 created response', function(done) {
            chai.request.agent(url)
                .post('/login')
                .send({ username: 'Alabi', password: 'p' })
                .then(function(res) {
                    assert.isDefined(res.body.data.token, 'Token for next request');
                    return chai.request.agent(url)
                        .patch('/patch')
                        .send({
                            document: { firstName: 'Albert', contactDetails: { phoneNumbers: [] } },
                            patch: [
                                { 'op': 'replace', 'path': '/firstName', 'value': 'Joachim' },
                                { 'op': 'add', 'path': '/lastName', 'value': 'Wester' },
                                { 'op': 'add', 'path': '/contactDetails/phoneNumbers/0', 'value': { 'number': '555-123' }  }
                            ]
                        })
                        .set('Authorization', res.body.data.token)
                        .then(function(res) {
                            const { statusCode, body: { data: { patchResult } } } = res;
                            assert.equal(statusCode, 201);
                            assert.typeOf(patchResult, 'object');
                            done();
                        });
                });
        });
    });

    /** @test {API#/thumbnail} */
    describe('/thumbnail', function() {
        this.timeout(5000);
        it('Should return a statuscode of 200', function(done) {
            chai.request.agent(url)
                .post('/login')
                .send({ username: 'Alabi', password: 'p' })
                .then(function(res) {
                    assert.isDefined(res.body.data.token, 'Token for next request');
                    return chai.request.agent(url)
                        .post('/thumbnail')
                        .set('Authorization', res.body.data.token)
                        .send({
                            url: 'https://homepages.cae.wisc.edu/~ece533/images/airplane.png'
                        })
                        .then(function(res) {
                            const { statusCode } = res;
                            assert.equal(statusCode, 200);
                            done();
                        });
                });
        });
    });
});

